import { useState, useEffect } from 'react';

function App() {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState('');

  // Load todos from localStorage when component mounts
  useEffect(() => {
    const storedTodos = JSON.parse(localStorage.getItem('todos')) || [];
    setTodos(storedTodos);
  }, []);

  // Save todos to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('todos', JSON.stringify(todos));
  }, [todos]);

  const handleAddTodo = () => {
    if (newTodo.trim()) {
      setTodos([...todos, { id: Date.now(), text: newTodo, completed: false }]);
      setNewTodo('');
    }
  };

  const handleDeleteTodo = (id) => {
    setTodos(todos.filter(todo => todo.id !== id));
  };

  const toggleComplete = (id) => {
    setTodos(
      todos.map(todo =>
        todo.id === id ? { ...todo, completed: !todo.completed } : todo
      )
    );
  };

  return (
    <div className="container mx-auto px-4 py-8 max-w-md">
      <h1 className="text-3xl font-bold mb-6 text-center text-gray-800">Todo App</h1>

      {/* Add Todo Form */}
      <div className="flex mb-6">
        <input
          type="text"
          value={newTodo}
          onChange={(e) => setNewTodo(e.target.value)}
          placeholder="Add a new todo..."
          className="flex-grow px-4 py-2 border rounded-l focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <button
          onClick={handleAddTodo}
          className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-r transition duration-200"
        >
          Add
        </button>
      </div>

      {/* Todo List */}
      <ul className="space-y-2">
        {todos.length > 0 ? (
          todos.map((todo) => (
            <li
              key={todo.id}
              className={`flex items-center justify-between p-3 bg-white rounded shadow-sm ${
                todo.completed ? 'opacity-75' : ''
              }`}
            >
              <div className="flex items-center space-x-3">
                <span
                  onClick={() => toggleComplete(todo.id)}
                  className={`w-6 h-6 flex items-center justify-center cursor-pointer border rounded-full ${
                    todo.completed
                      ? 'bg-green-500 text-white'
                      : 'border-gray-400'
                  }`}
                >
                  {todo.completed && <i className="fas fa-check text-xs"></i>}
                </span>
                <span className={`${todo.completed ? 'line-through text-gray-500' : ''}`}>
                  {todo.text}
                </span>
              </div>
              <button
                onClick={() => handleDeleteTodo(todo.id)}
                className="text-red-500 hover:text-red-700"
              >
                <i className="fas fa-trash-alt"></i>
              </button>
            </li>
          ))
        ) : (
          <li className="p-3 bg-white rounded shadow-sm text-center text-gray-500">
            No todos yet. Add one above!
          </li>
        )}
      </ul>
    </div>
  );
}

export default App;
